<template>
    <div class="cmui-textarea">
        <textarea
            :class="{
                'is-disabled': disabled
            }"
            :rows="rows"
            :value="currentValue"
            :placeholder="placeholder"
            :autofocus="autofocus"
            :disabled="disabled"
            @focus="handleFocus"
            @blur="handleBlur"
            @input="handleInput"></textarea>
        <div class="cmui-textarea-counter">
            <text class="cmui-textarea-text cmui-textarea-text-current">{{current}}</text>
            <text class="cmui-textarea-separate">{{separate}}</text>
            <text class="cmui-textarea-text">{{total}}</text>
        </div>
    </div>
</template>

<script>
    module.exports =  {
        name: 'CmuiTextarea',

        componentName: 'CmuiTextarea',

        props: {
            value: [String, Number],
            placeholder: {
                type: String,
                default: '请输入'
            },
            rows: {
                type: Number,
                default: 2
            },
            autofocus: Boolean,
            disabled: Boolean,
            maxlength: Number,
            current: {
                type: [String, Number],
                default: 0
            },
            separate: {
                type: String,
                default: '/'
            },
            total: {
                type: [String, Number],
                default: 200
            }
        },

        data() {
            return {
                currentValue: this.value
            }
        },

        watch: {
            'value' (val, oldValue) {
                this.setCurrentValue(val);
            }
        },

        methods: {
            handleBlur(event) {
                this.$emit('blur', event);
            },
            handleFocus(event) {
                this.$emit('focus', event);
            },
            handleInput(event) {
                const value = event.target.value;

                this.$emit('input', value);

                this.setCurrentValue(value);

                this.$emit('change', value);
            },
            setCurrentValue(value) {
                if (value === this.currentValue) {
                    return;
                }

                this.currentValue = value;
            }
        }
    }
</script>

<style lang="sass" scoped>
    @import './textarea-counter.scss';
</style>
